                    'sort_order' => $sortOrder,
                ];
            @endphp
            $(function() {
                $('#show_soft_deletes').change(function() {
                    if ($(this).prop('checked')) {
                        $('#dataTable').before('<a id="redir" href="{{ (route('voyager.'.$dataType->slug.'.index', array_merge($params, ['showSoftDeleted' => 1]), true)) }}"></a>');
                    }else{
                        $('#dataTable').before('<a id="redir" href="{{ (route('voyager.'.$dataType->slug.'.index', array_merge($params, ['showSoftDeleted' => 0]), true)) }}"></a>');
                    }

                    $('#redir')[0].click();
                })
            })
        @endif
        $('input[name="row_id"]').on('change', function () {
            var ids = [];
            $('input[name="row_id"]').each(function() {
                if ($(this).is(':checked')) {
                    ids.push($(this).val());
                }
            });
            $('.selected_ids').val(ids);
        });

        function changueprice(i) {
           var price =$(".newprice"+i).val();
           var id=i;
           $.post('/admin/posts/'+id+'/changueprice',{
            _token: "{{ csrf_token() }}",
            precio:price
           }, function(response) {
            if(response["status"]=="success"){
                toastr.success(response["message"]);
                return;
            }
            toastr.error(response["message"]);
           });
        }
        $(document).ready(function(){
            $(".changue-status").change(function() {
                $(this).find("option:selected").each(function() {
                    let params = $(this).val();
                    $.post("/admin/posts/changue_status", {
                        _token: "{{ csrf_token() }}",
                        params: params
                    }, function(data) {
                        if (data["status"] == "success") {
                            toastr.success(data["message"]);
                            return;
                        }
                        toastr.error(data["message"]);
                    });
                });
             });

            @if(isset($dataTypeContent->category_id))
                $.post("/admin/lista_subcategoria", { _token: "{{ csrf_token() }}" , subcategoryId:{{$dataTypeContent->subcategoryId}}, elegido: {{$dataTypeContent->category_id}} }, function(data){
                    $("#subcategoryId").html(data);
                });             
            @endif

        });
    </script>
